import React, {Component} from 'react';

import AppHeader from '../app-header/app-header';
import SearchPanel from '../search-panel/search-panel';
import TodoList from '../todo-list/todo-list';
import ItemStatusFilter from '../item-status-filter/item-status-filter';
import AddNewItem from '../input/input';

import './app.css';

export default class App extends Component {

    state = {
        todoData: [
            {
                label: 'Drink Coffee',
                important: false,
                id: 0
            }, {
                label: 'Make Awesome App',
                important: true,
                id: 1
            }, {
                label: 'Have a lunch',
                important: false,
                id: 2
            }
        ]
    };

    deleteItem = (id) => {
        this.setState(({todoData}) => {

            const idx = todoData.findIndex(el => el.id === id)

            const newArray = [
                ...todoData.slice(0, idx),
                ...todoData.slice(idx + 1)
            ]

            return {todoData: newArray}

        })

    }

    AddItem =() =>{
      this.setState(({todoData}) => {

        const defaultValue = [{
            label: 'Have a lunch',
            important: false,
            id: this.state.todoData[this.state.todoData.length-1].id + 1}]

            console.log(defaultValue.id);


        const updatedSet = [...todoData,...defaultValue]

        return {todoData: updatedSet}
      })
    }

    render() {
        return (
            <div className="todo-app">
                <AppHeader toDo={1} done={3}/>
                <div className="top-panel d-flex">
                    <SearchPanel/>
                    <ItemStatusFilter/>
                </div>

                <TodoList todos={this.state.todoData} onDeleted={this.deleteItem}/>
                <AddNewItem AddItem = {this.AddItem}/>

            </div>
        );
    };
};
